mapper.select:
select count(*) 
from OG_TestReport 
where 1=1

   AND id = #{id}


   AND project = #{project}


   AND lotNo = #{lotNo}


   AND dateTime = #{dateTime}


   AND sensorSerialNo = #{sensorSerialNo}

select * 
from OG_TestReport 
where 1=1

   AND id = #{id}


   AND project = #{project}


   AND lotNo = #{lotNo}


   AND dateTime = #{dateTime}


   AND sensorSerialNo = #{sensorSerialNo}

select * 
from OG_SensorReport 
where 1=1

   AND id = #{id}


   AND project = #{project}


   AND testReportId = #{testReportId}


   AND sensorNo = #{sensorNo}


   AND lotNo = #{lotNo}


   AND dateTime = #{dateTime}


   AND serialNo = #{serialNo}

select decisionCode ,count(decisionCode) as codeCount from ( select testReportId
,DATENAME(YYYY, dateTime) +'년 '+ DATENAME(MM, datetime) +'월 '+ DATENAME(DD, datetime) + '일' AS testDate
,DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(ww, dateTime) + '주' AS testDate
,DATENAME(yyyy, dateTime)+ '년 ' + DATENAME(MM, dateTime) + '월' AS testDate
,DATENAME(yyyy, dateTime)+ '년 ' AS testDate
,decisionCode from OG_sensorReport where 1=1
and dateTime >= #{fromDate}
and dateTime <= #{toDate}
)baseTbl where 1=1
and testDate = #{selectTestDate}
group by decisionCode
